      background-color: @base;
    }
    .info_box_row {
      background: @crust;
    }
    .info_box_row_label {
      color: @text;
    }
    .video-card-thumbnail__video-state-overlay {
      color: @text !important;
      background: fade(@mantle, 80%) !important;
    }

    /* Twitch alerts */

    .alerts-home-main-wrapper {
      color: @text !important;
    }

    .alerts-home-scrollable-area,
    .alert-boxes-section-border-container {
      color: @text !important;
      .simplebar-content {
        background: @mantle !important;
      }
      .alert-set-card {
        background: @crust !important;
      }
    }

    /* Analytics */

    .top-stats-tab--active {
      color: @accent !important;
      box-shadow: 0 calc(var(--border-width-default) * -3) 0 @accent inset;
      .top-stats-tab__title {
        color: @accent !important;
      }
    }

    .top-stats-tab:hover {
      box-shadow: inset 0 -2px 0 @accent, 0 4px 6px -4px @accent;
      background: @base !important;
    }

    /* Stream Manager */

    .stream-manager--page-view {
      .mosaic-tile .simplebar-content,
      .stream-manager-edit-mode-item,
      .stream-manager-edit-mode-stat-item {
        background: @mantle !important;
      }
    }

    /* Autohost / managed channels list */

    .dashboard-centered-page {
      --color-background-alt: @base;
      .simplebar-scroll-content,
      .simplebar-content {
        background: transparent !important;
      }

      .autohost-list-item--hovered {
        color: @text;
        background: @base;

        button svg path {
          fill: @text !important;
        }
      }
    }

    /* Research */

    .time-to-stream-tab--active {
      box-shadow: 0 calc(var(--border-width-default) * -3) 0 @accent inset;
    }

    /* Collection thumbnails */

    .collection-preview-image__wrapper {
      color: @text !important;
      background: linear-gradient(
        90deg,
        fade(@mantle, 0%),
        fade(@mantle, 100%) 75%
      );
    }

    /* AutoMod shadow */

    .automod-level-controls__categories--bottom::after {
      background-image: linear-gradient(fade(@mantle, 0%), @mantle);
    }

    /* Tables */

    .tw-table,
    .tw-table-heading {
      border-color: @surface0;
      background: @crust;
    }

    /* Achievements & activity page */

    .ach-sb,
    .activity-page__scrollable .simplebar-content {
      background: @mantle !important;
    }

    /* Clips player background */

    .clips-player-container {
      background: @crust;
    }

    .clmgr-table__row {
      &:hover {
        background: @surface0 !important;
      }
    }

    /* Announcements button */

    .announcements-icon--green svg {
      fill: @green !important;
    }

    /* Achievements */

    .ach-q-item {
      &.ach-q-item--expanded {
        box-shadow: 0 0 0 1px @accent;
      }

      [data-test-selector="is-affiliate-banner"] {
        span,
        a {
          color: @base !important;
        }
      }

      .quest-progress-bars__incomplete {
        background-color: @surface0 !important;
      }
    }

    [data-target="video-card"] {
      background: @mantle !important;
    }

    /* Stream Manager quick actions */

    .quick-action,
    .quick-actions-store-card-layout--top {
      color: @base;

      &.quick-action--purple,
      &.quick-actions-store-card-layout--purple {
        background-color: @mauve;
      }

      &.quick-action--blue,
      &.quick-actions-store-card-layout--blue {
        background-color: @blue;
      }

      &.quick-action--magenta,
      &.quick-actions-store-card-layout--magenta {
        background-color: @pink;
      }

      &.quick-action--green,
      &.quick-actions-store-card-layout--green {
        background-color: @green;
      }

      &.quick-action--red,
      &.quick-actions-store-card-layout--red {
        background-color: @red;
      }

      &.quick-action-layout--disabled {
        color: @subtext0;
        background-color: @surface0;
      }
    }

    /* Stream Manager statistics */
    .sunlight-tile {
      background-color: @crust;
    }

    .creator-home-card__icon-background--yellow {
      background-color: @yellow;
    }
  }
}

@-moz-document domain("dev.twitch.tv"),
  url-prefix("https://discuss.dev.twitch.com/embed/topics") {
  :root {
    @media (prefers-color-scheme: light) {
      #catppuccin(@lightFlavor);
    }
    @media (prefers-color-scheme: dark) {
      #catppuccin(@darkFlavor);
    }
  }

  #catppuccin(@flavor) {
    @rosewater: @catppuccin[@@flavor][@rosewater];
    @flamingo: @catppuccin[@@flavor][@flamingo];
    @pink: @catppuccin[@@flavor][@pink];
    @mauve: @catppuccin[@@flavor][@mauve];
    @red: @catppuccin[@@flavor][@red];
    @maroon: @catppuccin[@@flavor][@maroon];
    @peach: @catppuccin[@@flavor][@peach];
    @yellow: @catppuccin[@@flavor][@yellow];
    @green: @catppuccin[@@flavor][@green];
    @teal: @catppuccin[@@flavor][@teal];
    @sky: @catppuccin[@@flavor][@sky];
    @sapphire: @catppuccin[@@flavor][@sapphire];
    @blue: @catppuccin[@@flavor][@blue];
    @lavender: @catppuccin[@@flavor][@lavender];
    @text: @catppuccin[@@flavor][@text];
    @subtext1: @catppuccin[@@flavor][@subtext1];
    @subtext0: @catppuccin[@@flavor][@subtext0];
    @overlay2: @catppuccin[@@flavor][@overlay2];
    @overlay1: @catppuccin[@@flavor][@overlay1];
    @overlay0: @catppuccin[@@flavor][@overlay0];
    @surface2: @catppuccin[@@flavor][@surface2];
    @surface1: @catppuccin[@@flavor][@surface1];
    @surface0: @catppuccin[@@flavor][@surface0];
    @base: @catppuccin[@@flavor][@base];
    @mantle: @catppuccin[@@flavor][@mantle];
    @crust: @catppuccin[@@flavor][@crust];
    @accent: @catppuccin[@@flavor][@@accentColor];

    --ctp-rosewater: @rosewater;
    --ctp-flamingo: @flamingo;
    --ctp-pink: @pink;
    --ctp-mauve: @mauve;
    --ctp-red: @red;
    --ctp-maroon: @maroon;
    --ctp-peach: @peach;
    --ctp-yellow: @yellow;
    --ctp-green: @green;
    --ctp-teal: @teal;
    --ctp-sky: @sky;
    --ctp-sapphire: @sapphire;
    --ctp-blue: @blue;
    --ctp-lavender: @lavender;
    --ctp-text: @text;
    --ctp-subtext1: @subtext1;
    --ctp-subtext0: @subtext0;
    --ctp-overlay2: @overlay2;
    --ctp-overlay1: @overlay1;
    --ctp-overlay0: @overlay0;
    --ctp-surface2: @surface2;
    --ctp-surface1: @surface1;
    --ctp-surface0: @surface0;
    --ctp-base: @base;
    --ctp-mantle: @mantle;
    --ctp-crust: @crust;

    &,
    body {
      color: @text !important;
      background: @base;
      --primary-medium: @text;
      --primary-low: @surface0;
      scrollbar-color: @surface0 @mantle;
    }

    a.btn,
    button.btn {
      border-color: @accent;
      background: @accent !important;
      color: @base !important;
    }

    a.btn:hover,
    button.btn:hover {
      background: fade(@accent, 80%) !important;
    }

    /* Recent announcements */
    .topics-list .topic-list-item .main-link a {
      color: @accent;
    }

    .topic-created-at,
    .topic-last-posted-at,
    .topic-like-count,
    .topic-post-count {
      color: @subtext0 !important;
    }

    /* Header */
    .nav__container,
    .dev-top-nav {
      background: @crust !important;

      .tw-link,
      .dev-top-nav__nav-items-container {
        color: @text !important;
        background: @crust !important;
      }
      .online {
        border-color: @crust !important;
      }
      a.btn.light {
        background: fade(@accent, 10%) !important;
        color: @text !important;
      }
      .nav__links a {
        color: @text;

        &:hover {
          color: @subtext0;
        }
        &.active {
          color: @accent;
          border-color: @accent;
        }
      }
      .nav__logo svg path {
        fill: @text;
      }
    }

    /* Footers */
    .footer,
    .dev-footer,
    .subscribe-footer {
      h5,
      p,
      a,
      div {
        color: @text !important;
      }
      a:hover {
        color: @accent !important;
      }

      svg path {
        fill: @text !important;
      }

      background: @crust;
    }

    .bright-cta {
      * {
        color: @mantle !important;
      }
      background: @accent;
    }
    .content-alternate-2 {
      background: @base;
    }

    .sandbox-tab {
      color: @text !important;
    }

    .why-twitch ul li {
      background: @base;
      border-color: @surface0 !important;
    }

    .sandbox-tab.active {
      color: @accent !important;
      border-color: @accent !important;
    }

    .hero,
    .extension-cta,
    .subscribe-footer {
      background: @mantle;
    }

    thead tr th {
      border-color: @surface0;
      background: @mantle;
    }

    tbody tr td {
      border-color: @surface0;
    }

    tbody tr:nth-child(odd) {
      background: @base !important;
    }
    tbody tr:nth-child(even) {
      background: @mantle !important;
    }

    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    hr {
      color: @text !important;
      border-color: @surface0 !important;
    }

    .right-code {
      p {
        color: @subtext0;
      }
      background-color: @base !important;
    }

    blockquote {
      border-color: @accent;
      background: @mantle;
    }

    .content,
    .main,
    .topics-list,
    .doc-content {
      background: @base;
      border-color: @base !important;
      a {
        color: @accent;
      }
      border-bottom-color: @surface0 !important;
      border-image: none;
      -webkit-border-image: none;
    }

    code {
      border-color: @accent !important;
      background: fade(@accent, 5%) !important;
      color: @text !important;
    }

    /* Code */
    .right-code pre,
    pre.highlight,
    pre {
      border-color: @mantle !important;
      background: @mantle !important;
      code {
        background: transparent !important;
        color: @text !important;
      }
    }

    /* Pills */
    .pill-new {
      color: @base !important;
      background: @accent !important;
    }
    .pill-beta {
      color: @base !important;
      background: @yellow !important;
    }

    /* Navbar */
    .sidebar {
      background: @mantle !important;
      dl {
        border-color: @base !important;
      }
      dt a,
      dl dd a {
        color: @text !important;
      }
      dl dd a.active-highlight {
        color: @accent !important;
      }
      dl dd a.active-highlight::before {
        border-left-color: @accent !important;
      }
      dt a:hover {
        background: @base !important;
      }

      /* Search */

      input {
        color: @text;
        &::placeholder {
          color: @subtext0;
        }
        &:focus {
          background: @crust !important;
          box-shadow: 0 0 3px 1px fade(@accent, 70%);
          color: @text;
        }
      }

      .search-icon svg path {
        fill: @text !important;
      }

      .algolia-autocomplete {
        .algolia-docsearch-suggestion--category-header {
          color: @text !important;
          background: @mantle !important;
          border-color: @base !important;
        }

        .algolia-docsearch-suggestion--highlight {
          background: @accent !important;
          color: @base !important;
        }

        .algolia-docsearch-suggestion--content {
          background: @mantle !important;
        }

        .algolia-docsearch-suggestion--title {
          color: @text !important;
        }

        .algolia-docsearch-suggestion--text {
          color: @subtext0 !important;
        }

        .ds-suggestion.ds-cursor .algolia-docsearch-suggestion--content {
          .algolia-docsearch-suggestion--title,
          .algolia-docsearch-suggestion--text {
            background: @crust !important;
            .algolia-docsearch-suggestion--highlight {
              background: @base !important;
            }
          }
        }
      }
    }

    ul.grid li.story {
      h4 a,
      a.btn {
        color: @text !important;
      }
      a {
        color: @accent !important;
      }
      .story__links a {
        color: @base !important;
      }

      border-color: @surface0;
    }

    .submission .relative .outer {
      svg path {
        fill: @text;
      }
      button {
        border-color: @accent !important;
      }

      .tag button {
        background: @accent !important;
        border-color: @accent !important;
      }

      .author {
        color: @subtext0 !important;
      }
      background: @mantle;
    }
  }
}

/* deno-fmt-ignore */
