      #bsButtonOutline(@sapphire, @crust);
    }
    .btn-outline-warning {
      #bsButtonOutline(@yellow, @crust);
    }
    .btn-outline-danger {
      #bsButtonOutline(@red, @text);
    }
    .btn-outline-light {
      #bsButtonOutline(@text, @crust);
    }
    .btn-outline-dark {
      #bsButtonOutline(@surface0, @text);
    }

    .text-success {
      color: @green !important;
