      @light: fade(@surface1, 40%);
      @svg: escape(
        '<svg width="64" height="64" xmlns="http://www.w3.org/2000/svg"><path fill="@{dark}" d="M0 0h32v32H0z"/><path fill="@{light}" d="M32 0h32v32H32zM0 32h32v32H0z"/><path fill="@{dark}" d="M32 32h32v32H32z"/></svg>'
      );
      background-image: url("data:image/svg+xml,@{svg}");
    }

    /* skins page nav buttons */
    .nav.nav-pills {
      .nav-item .nav-link a {
        color: @text !important;
      }
      .nav-link.active {
        background-color: @accent !important;
        color: @crust !important;
      }
    }

    .page-item .page-link {
      background-color: @surface0 !important;
      border-color: @surface2 !important;
      color: @text;

      &.active {
        background-color: @accent !important;
      }

      &:focus,
      &:hover {
        box-shadow: none !important;
        color: @accent !important;
      }
    }

    .page-item.active .page-link {
      background-color: @accent !important;
      border-color: @accent !important;
      color: @crust !important;
    }

    /* single skin page */
    a.badge,
    span.badge {
      background-color: @accent !important;
      color: @crust !important;
    }

    /* usernames page */
    form#upcoming-form {
      div.row {
        label.col-form-label {
          color: @text !important;
        }

        div.col {
          .input-group {
            .input-group-text {
              background-color: @base !important;
              border-color: @surface2 !important;
            }
          }
        }
      }
    }

    .form-control,
    .form-select {
      background-color: @base !important;
      border-color: @surface2 !important;
      box-shadow: none !important;
      color: @subtext1 !important;

      &:focus {
        border-color: @accent !important;
        box-shadow: 0 0 0 0.25rem fade(@accent, 25%);
      }
    }

    /* server list page */
    .mc-red {
      color: @maroon !important;
    }
    .mc-dark-red {
      color: @red !important;
    }
    .mc-gold {
      color: @peach !important;
    }
    .mc-yellow {
      color: @yellow !important;
    }
    .mc-green,
    .mc-dark-green {
      color: @green !important;
    }
    .mc-aqua {
      color: @sky !important;
    }
    .mc-dark-aqua {
      color: @teal !important;
    }
    .mc-blue {
      color: @sapphire !important;
    }
    .mc-dark-blue {
      color: @blue !important;
    }
    .mc-light-purple {
      color: @pink !important;
    }
    .mc-dark-purple {
      color: @mauve !important;
    }

    /* profile page */
    .namemc-rank-10 {
      color: @green !important;
    }

